//###################################################################################################
// Create Function

module.exports.create = function(_config, _store) {
    return new Handler(_config, _store);
};

//###################################################################################################
// Basic Object
function Handler(_config, _store) {
    /////////////////////////////////////////////////////////////////////////////////////////////////
   
    var 
    ////=============================================================================================
    // Requirements
    
    ////=============================================================================================
    // Properties
    
        self = this,
        config,
        store,
    
    ////=============================================================================================
    // Methods
    
        ////-----------------------------------------------------------------------------------------
        // Initialize
        init = function(_config, _store) {
            
            if( _config !== undefined )
                config = _config;
            else
                console.error('Options undefined');
                
            if( _store !== undefined )
                store = _store;
            else
                console.error('Store undefined');
            
            events.EventEmitter.call(this);
                        
        },        
        
        ////-----------------------------------------------------------------------------------------
        // IRC Command
        onIRCCommand = function(from, to, command) {
            var args = command.split(' ');
            
            if( args[0] === 'add' ) {
                store.emit('set', 'command.'+args[1], command.substr(args[0].length+args[1].length+2));
            }
            else if( args[0] === 'del' ) {
                store.emit('remove', 'command.'+args[1]);
            }
            else {
                store.emit('get', 'command.'+args[0], function(value) {
                if( value != undefined )
                    eval(value);
            });
            }
        }

    ////=============================================================================================
    ;

    /////////////////////////////////////////////////////////////////////////////////////////////////

    init(_config, _store);
    
    /////////////////////////////////////////////////////////////////////////////////////////////////
}

//###################################################################################################
// Inherit from EventEmitter

events = require('events');
Handler.super_ = events.EventEmitter;
Handler.prototype = Object.create(events.EventEmitter.prototype, {
    constructor: {
        value: Handler,
        enumerable: false
    }   
});
