//###################################################################################################
// Create Function

module.exports.create = function(_config) {
    return new Handler(_config);
};

//###################################################################################################
// Basic Object
function Handler(_config) {
    /////////////////////////////////////////////////////////////////////////////////////////////////
   
    var 
    ////=============================================================================================
    // Requirements
        
        nstore = require('nstore'),
    
    ////=============================================================================================
    // Propertys
    
        self = this,
        dataStore,
        config,
    
    ////=============================================================================================
    // Methods
    
        ////-----------------------------------------------------------------------------------------
        // Function
        init = function(_config) {
            
            if( _config !== undefined )
                config = _config;
            else
                console.error('[storeHandler] Options undefined');
            
            events.EventEmitter.call(this);
                        
            dataStore = nStore.new(config.file);
            
            self.on('get', get);
            self.on('save', save);
            self.on('find', find);
            self.on('remove', remove);
            self.on('clear', clear);
        },
        
        get = function(name, callback) {
        },
        
        save = function(name, object, callback) {
        },
        
        find = function(term, callback) {
        },
        
        remove = function(name, callback) {            
        }
        
        clear = function(callback) {
            dataStore.clear();   
        }

    ////=============================================================================================
    ;

    /////////////////////////////////////////////////////////////////////////////////////////////////

    init(_config);
    
    /////////////////////////////////////////////////////////////////////////////////////////////////
}

//###################################################################################################
// Inherit from EventEmitter

events = require('events');
Handler.super_ = events.EventEmitter;
Handler.prototype = Object.create(events.EventEmitter.prototype, {
    constructor: {
        value: Handler,
        enumerable: false
    }   
});
